changeSet.sql:
CREATE TABLE IF NOT EXISTS coffee_admin
(
    id       serial PRIMARY KEY,
    username text
);
CREATE TABLE IF NOT EXISTS drink
(
    id    serial PRIMARY KEY,
    name  text,
    price numeric,
    count numeric
);
CREATE TABLE IF NOT EXISTS coffee_user
(
    id       serial PRIMARY KEY,
    username text,
    amount   numeric
);

insert into public.coffee_admin (id, username)
values (1, 'myadmin');
insert into public.coffee_user (id, username, amount)
values (1, 'myuser', 1000);
insert into public.drink (id, name, price, count)
values (2, 'Tee', 2, 200),
       (3, 'Americano', 4, 50),
       (1, 'Coffee', 3, 100);
