changeSet.sql:
create table if not exists coffee_admin
(
    id       int8 generated by default as identity,
    username varchar(255),
    primary key (id)
);
create table if not exists coffee_user
(
    id       int8 generated by default as identity,
    username varchar(255),
    amount   int8,
    primary key (id)
);
create table if not exists drink
(
    id    int8 generated by default as identity,
    count int8,
    name  varchar(255),
    price int8,
    primary key (id)
);

insert into coffee_admin (id, username)
values (1, 'myadmin');
insert into coffee_user (id, username, amount)
values (1, 'myuser', 1000);
insert into drink (id, name, price, count)
values (2, 'Tee', 2, 200),
       (3, 'Americano', 4, 50),
       (1, 'Coffee', 3, 100);
